                  required
                />
                <div style={{
                  marginTop:8,
                  padding:10,
                  background:'rgba(59,130,246,0.1)',
                  border:'1px solid rgba(59,130,246,0.3)',
                  borderRadius:8,
                  fontSize:11,
                  color:'rgba(148,163,184,0.9)'
                }}>
                  ðŸ’¡ Open your authenticator app (Google Authenticator, Microsoft Authenticator, or Authy) and enter the 6-digit code shown for ATIS Transport.
                </div>
              </div>
            )}

            {error && (
              <div style={{padding:12, background:'rgba(239, 68, 68, 0.1)', border:'1px solid var(--danger)', borderRadius:12, color:'var(--danger)', fontSize:13, textAlign:'center'}}>
                âš ï¸ {error}
              </div>
            )}

            <button 
              type="submit" 
              className="btn btn-primary" 
              style={{width:'100%', padding:16, fontSize:16}}
              disabled={loading}
            >
              {loading ? 'â³ Processing...' : mode === 'login' ? 'ðŸš€ Sign In' : 'âœ¨ Create Account'}
            </button>

            <div style={{textAlign:'center'}}>
              <button 
                type="button"
                onClick={() => {setMode(mode === 'login' ? 'register' : 'login'); setError('')}}
                style={{background:'none', border:'none', color:'var(--primary)', cursor:'pointer', fontSize:14, fontWeight:600}}
                disabled={loading}
              >
                {mode === 'login' ? "Don't have an account? Sign up" : 'Already have an account? Sign in'}
              </button>
            </div>
          </form>
        </div>

        {/* Features Preview */}
        <div style={{marginTop:32, textAlign:'center', color:'var(--muted)', fontSize:13}}>
          <p style={{marginBottom:12}}>âœ¨ Real-time traffic & transit data</p>
          <p style={{marginBottom:12}}>ðŸ—ºï¸ Interactive route planning</p>
          <p>ðŸŒ Multi-language support</p>
        </div>
      </div>

      <style>{`
        @keyframes float {
          0%, 100% { transform: translateY(0) scale(1); }
          50% { transform: translateY(-20px) scale(1.05); }
        }
      `}</style>
    </div>
  )
}

export default function App(){
