            <div style={{fontSize:13, fontWeight:600}}>{username}</div>
            <div style={{fontSize:11, color:'var(--muted)'}}>Verified</div>
          </div>
        </div>
        <button className="btn btn-danger" onClick={logout}>ðŸšª Logout</button>
