              >
                {v === 'home' && 'ðŸ '} {v === 'plan' && 'ðŸ—ºï¸'} {v === 'map' && 'ðŸŒ'}
                {v === 'alerts' && 'âš ï¸'} {v === 'safety' && 'ðŸ›¡ï¸'} {v === 'reviews' && 'â­'}
                {v === 'analytics' && 'ðŸ“Š'} {v === 'settings' && 'âš™ï¸'} {' '}{v.charAt(0).toUpperCase() + v.slice(1)}
              </button>
